package bastanteo;

public class Rol {
	
	String codigoRol;
	String descripcion;

	public Rol(String codigoRol, String descripcion) {
		// TODO Auto-generated constructor stub
		this.codigoRol = codigoRol;
		this.descripcion = descripcion;
	}
	
	public String getCodigoRol() {
		return codigoRol;
	}
	
	public String getDescripcion() {
		return descripcion;
	}
	
}